DROP TABLE IF EXISTS `admin_user`;
CREATE TABLE `admin_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(20) COLLATE utf8_general_mysql500_ci NOT NULL COMMENT '后台用户名',
  `pass` char(32) COLLATE utf8_general_mysql500_ci NOT NULL COMMENT '密码',
  `addtime` int(10) unsigned NOT NULL,
