DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `stitle` varchar(255) DEFAULT NULL COMMENT '副标题',
  `addtime` int(10) unsigned NOT NULL COMMENT '添加时间',
  `cateid` int(8) DEFAULT NULL COMMENT '所属分类ID',
  `ms` mediumtext COMMENT '文章详情',
  `img` varchar(200) DEFAULT NULL COMMENT '封面图',
  `orders` int(8) DEFAULT '0' COMMENT '排序',
  `ist` tinyint(1) DEFAULT '0' COMMENT '是否推荐，0不是，1推荐',
  `desc` mediumtext COMMENT '文章描述',
  `click` int(11) DEFAULT '100' COMMENT '浏览次数',
  `publishtime` int(11) DEFAULT '0' COMMENT '发布时间',
  `en_desc` text,
  `en_ms` text,
  PRIMARY KEY (`id`),
  UNIQUE KEY `title` (`title`),
  KEY `cateid` (`cateid`),
  KEY `orders` (`orders`),
  KEY `ist` (`ist`)
) ENGINE=InnoDB AUTO_INCREMENT=157 DEFAULT CHARSET=utf8 COMMENT='文章表';

